mapper.select:
select seq , mem_id , mem_name , board_subject , board_content , reg_date , upt_date , view_cnt FROM (SELECT Tb.*, rownum rNum from ( SELECT * FROM board_study WHERE 1=1
AND ${searchField} LIKE '%'||#{searchTxt}||'%'
AND BOARD_CONTENT LIKE '%'||#{searchTxt}||'%' OR BOARD_SUBJECT LIKE '%'||#{searchTxt}||'%'
AND REG_DATE >= #{startDate }
AND REG_DATE <= #{endDate }
ORDER BY seq DESC ) Tb ) WHERE rNum BETWEEN ${start} AND ${end}

mapper.insert:
INSERT INTO board_study
(
	seq
	, mem_id
	, mem_name
	, board_subject 
	, board_content
	, reg_date
	, view_cnt
)
VALUES
(
	(SELECT NVL(MAX(seq), 0)+1 FROM board_study)
	, #{memId}
	, #{memName}
	, #{boardSubject}
	, #{boardContent} 
	, sysdate
	, 0
)

mapper.update:
	UPDATE board_study SET
		  mem_id 		=	#{memId }
		, mem_name 		=	#{memName }
		, board_subject =	#{boardSubject }
		, board_content =	#{boardContent }
		, upt_date 		=	sysdate
	WHERE seq=#{seq }

mapper.delete:

DELETE FROM board_study 

WHERE seq in
 
 	#{checkbox.value }

mapper.select:
SELECT COUNT(*) FROM board_study WHERE 1=1
AND ${searchField} LIKE '%'||#{searchTxt}||'%'
OR BOARD_SUBJECT LIKE '%'||#{searchTxt}||'%'
AND REG_DATE >= #{startDate }
AND REG_DATE <= #{endDate }
